import { t } from '../../i18n.es.js';
import { notification } from 'antd';

const IMAGE_VALID_TYPES = [
  "image/jpeg",
  "image/png",
  "image/jpg",
  "image/gif",
  "image/webp"
];
const beforeUploadImage = (file) => {
  const isValidType = IMAGE_VALID_TYPES.includes(file.type);
  if (!isValidType) {
    notification.error({
      message: t("B\u1EA1n ch\u1EC9 c\xF3 th\u1EC3 t\u1EA3i l\xEAn \u1EA3nh d\u1EA1ng JPG/JPEG/PNG/GIF/WEBP!")
    });
    return false;
  }
  return true;
};

export { beforeUploadImage };
