import { getStyle, Alignment } from '@tempi/core-renderer';

const getAccordionItemChildrenCss = ({
  headerStyle,
  contentStyle,
  contentAlign
}) => {
  const getHorizontalAlign = () => {
    if (!contentAlign)
      return "";
    let alignStyle = "stretch";
    if (contentAlign === Alignment.center)
      alignStyle = "center";
    if (contentAlign === Alignment.right)
      alignStyle = "flex-end";
    return `align-items: ${alignStyle};`;
  };
  return `
  .accordion {
    &__header {
      ${getStyle(headerStyle)}
    }

    &__content {
      ${getStyle(contentStyle)}
      ${getHorizontalAlign()}
    }
  }
`;
};
const convertTitleToText = (title) => {
  const regexReplaceHtmlTag = /<([^>]+)>/gm;
  return (title || "").replace(regexReplaceHtmlTag, "");
};

export { convertTitleToText, getAccordionItemChildrenCss };
