import { t } from '../../i18n.es.js';
import { Alignment } from '@tempi/core-renderer';

const getStartOfDay = (date) => {
  return new Date(date.setHours(0, 0, 0, 0));
};
const DEFAULT_COUNTDOWN = {
  countdownType: "due",
  due: getStartOfDay(new Date()).toISOString(),
  showDays: true,
  showHours: true,
  showMinutes: true,
  showSeconds: true,
  showLabel: true,
  labelDays: t("Ng\xE0y"),
  labelHours: t("Gi\u1EDD"),
  labelMinutes: t("Ph\xFAt"),
  labelSeconds: t("Gi\xE2y"),
  countdownStyle: {
    elementGap: 20,
    labelGap: 0,
    align: Alignment.left,
    layout: "vertical",
    labelPosition: "baseline",
    digitStyle: {
      fontSize: 40
    },
    labelStyle: {
      textAlign: "center"
    },
    timeUnitStyle: {
      padding: {
        top: 10,
        bottom: 10,
        left: 10,
        right: 10
      }
    }
  }
};

export { DEFAULT_COUNTDOWN, getStartOfDay };
