import { withSelector, AdvanceAttributes, getEditorName } from '@tempi/core-editor';
import { DEFAULT_COUNTDOWN } from './constants.es.js';
import { CountdownQuickSetting } from './quick-setting.es.js';
import { Countdown } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const CountdownEditor = withSelector(Countdown, {
  displayName: "\u0110\u1EBFm ng\u01B0\u1EE3c",
  tag: Countdown.displayName.toLowerCase(),
  advanceAttributes: AdvanceAttributes,
  quickSetting: CountdownQuickSetting,
  props: DEFAULT_COUNTDOWN,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { CountdownEditor };
