import { withSelector, getEditorName } from '@tempi/core-editor';
import { DEFAULT_EXAM_SCORE } from './constants.es.js';
import { QuickSetting } from './quick-setting.es.js';
import { Setting } from './setting.es.js';
import { ExamScore } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const ExamScoreEditor = withSelector(ExamScore, {
  displayName: "Exam score",
  tag: ExamScore.displayName.toLowerCase(),
  quickSetting: QuickSetting,
  customAttributes: Setting,
  props: DEFAULT_EXAM_SCORE,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { ExamScoreEditor };
