import styled from '@emotion/styled';
import { getStyle } from '@tempi/core-renderer';
import { MOBILE_WIDTH } from '../../constants.es.js';

const Content = styled.div(
  ({ grid, commonStyle }) => {
    const {
      gridArea,
      navigationGridTemplateColumns,
      width,
      mobileNavigationGridTemplateColumns
    } = grid;
    return `
    position: relative;
    display: grid;
    grid-area: ${gridArea};
    grid-template-columns: ${navigationGridTemplateColumns};
    align-items: center;
    width: 100%;
    max-width: ${width || 1240}px;
    margin: 0 auto;
    
    ${getStyle({ padding: commonStyle.padding })};
  
    @media screen and (max-width: ${MOBILE_WIDTH}px){
      grid-template-columns: ${mobileNavigationGridTemplateColumns};
    }
  `;
  }
);

export { Content };
