import styled from '@emotion/styled';

const Logo = styled.a(({ grid }) => {
  const {
    logoGridRow,
    logoGridColumn,
    logoJustifySelf,
    logoSpacing,
    logoWidth
  } = grid;
  return `
    display: inline-flex;
    grid-row: ${logoGridRow};
    grid-column: ${logoGridColumn};
    align-self: center;
    justify-self: ${logoJustifySelf};
    margin: ${logoSpacing};
    width: ${logoWidth}px;
  `;
});
const LogoImg = styled.img(({ grid }) => {
  const { logoImageObjectPosition } = grid;
  return `
    width: 100%;
    height: 100%;
  
    vertical-align: middle;
    object-fit: contain;
    object-position: ${logoImageObjectPosition};
  `;
});

export { Logo, LogoImg };
