import styled from '@emotion/styled';
import { MOBILE_WIDTH } from '../../constants.es.js';

const Navbar = styled.nav(({ grid }) => {
  const {
    linksGridRow,
    linksGridColumn,
    navigationJustifySelf,
    menuItemSpacing
  } = grid;
  return `
    grid-row: ${linksGridRow};
    grid-column: ${linksGridColumn};
    justify-self: ${navigationJustifySelf};
    height: 100%;
    margin-left: ${-1 * menuItemSpacing}px;
  
    @media screen and (max-width: ${MOBILE_WIDTH}px){
      grid-row: unset;
      grid-column: unset;
    };
  `;
});

export { Navbar };
