import { withSelector, AdvanceAttributes, getEditorName } from '@tempi/core-editor';
import { DEFAULT_NUMBER_COUNTER } from './constants.es.js';
import { NumberCounterQuickSetting } from './quick-setting.es.js';
import { NumberCounterSetting } from './setting.es.js';
import { NumberCounter } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const NumberCounterEditor = withSelector(NumberCounter, {
  displayName: "Progress",
  tag: NumberCounter.displayName.toLowerCase(),
  advanceAttributes: AdvanceAttributes,
  quickSetting: NumberCounterQuickSetting,
  customAttributes: NumberCounterSetting,
  props: DEFAULT_NUMBER_COUNTER,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { NumberCounterEditor };
