import { NumberType } from './type.es.js';

const getMaxValue = (value, total, type) => {
  let maxValue = total;
  if (type === NumberType.percent) {
    maxValue = 100;
  }
  return Math.max(value, maxValue);
};
const calculatePercent = (value, maxValue) => {
  return Math.min(100, Math.round(value / maxValue * 1e4) / 1e4 * 100);
};

export { calculatePercent, getMaxValue };
