import { withSelector, AdvanceAttributes, getEditorName } from '@tempi/core-editor';
import { DEFAULT_QR_CODE } from './constants.es.js';
import { QuickSetting } from './quick-setting.es.js';
import { Setting } from './setting.es.js';
import { StyleSetting } from './style-setting.es.js';
import { QRCode } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const QRCodeEditor = withSelector(QRCode, {
  displayName: "QR code",
  tag: QRCode.displayName.toLowerCase(),
  quickSetting: QuickSetting,
  advanceAttributes: AdvanceAttributes,
  commonAttributes: StyleSetting,
  customAttributes: Setting,
  props: DEFAULT_QR_CODE,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { QRCodeEditor };
