import { withSelector, AdvanceAttributes, getEditorName } from '@tempi/core-editor';
import { DEFAULT_SOCIAL_ICON } from './constants.es.js';
import { DataSetting } from './data-setting.es.js';
import { SocialIconQuickSetting } from './quick-setting.es.js';
import { StyleSetting } from './style-setting.es.js';
import { SocialIcon } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const SocialIconEditor = withSelector(SocialIcon, {
  displayName: "Icon m\u1EA1ng x\xE3 h\u1ED9i",
  tag: SocialIcon.displayName.toLowerCase(),
  advanceAttributes: AdvanceAttributes,
  commonAttributes: StyleSetting,
  customAttributes: DataSetting,
  quickSetting: SocialIconQuickSetting,
  props: DEFAULT_SOCIAL_ICON,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { SocialIconEditor };
