import { mappingAssets } from '@tempi/core-renderer';
import { useState, useEffect } from 'react';

const useGetCitiesAndDistricts = () => {
  const [VN, setVN] = useState({});
  useEffect(() => {
    const getVNJsonFile = async () => {
      try {
        const response = await fetch(mappingAssets.VN);
        const jsonResponse = await response.json();
        setVN(jsonResponse);
      } catch (e) {
        console.error(e);
      }
    };
    getVNJsonFile();
  }, []);
  const cities = VN["province"] || [];
  const districts = VN["district"] || [];
  return { cities, districts };
};

export { useGetCitiesAndDistricts };
