import { withSelector, AdvanceAttributes, getEditorName } from '@tempi/core-editor';
import { DEFAULT_STORE_LIST } from './constants.es.js';
import { StoreListQuickSetting } from './quick-setting.es.js';
import { StoreListSetting } from './setting.es.js';
import { StoreList } from './ui.es.js';
import { RowEditor } from '../section/selector.es.js';
import { RootEditor } from '../root/selector.es.js';

const StoreListEditor = withSelector(StoreList, {
  displayName: "Danh s\xE1ch c\u1EEDa h\xE0ng",
  tag: StoreList.displayName.toLowerCase(),
  advanceAttributes: AdvanceAttributes,
  quickSetting: StoreListQuickSetting,
  customAttributes: StoreListSetting,
  props: DEFAULT_STORE_LIST,
  rules: {
    canDrop: (targetNode) => {
      const ignoreTargets = [
        getEditorName({ RowEditor }),
        getEditorName({ RootEditor })
      ];
      return !ignoreTargets.includes(targetNode.data.name);
    }
  }
});

export { StoreListEditor };
