import { jsx, jsxs } from 'react/jsx-runtime';
import styled from '@emotion/styled';
import { formatCurrencyVND } from '@tempi/core-renderer';

const ProductPrice = ({
  supplierRetailPrice,
  latestPrice,
  textColor,
  align = "start"
}) => {
  const formattedRetailPrice = formatCurrencyVND(supplierRetailPrice);
  const formattedLatestPrice = formatCurrencyVND(latestPrice);
  const renderedLatestPrice = /* @__PURE__ */ jsx(StyledLatestPrice, {
    color: textColor,
    children: formattedLatestPrice
  });
  const renderedRetailPrice = /* @__PURE__ */ jsx(StyledRetailPrice, {
    children: formattedRetailPrice
  });
  return /* @__PURE__ */ jsxs(ProductPriceWrapper, {
    align,
    children: [
      renderedLatestPrice,
      /* @__PURE__ */ jsx(StyledDiscount, {
        children: supplierRetailPrice && supplierRetailPrice > latestPrice && renderedRetailPrice
      })
    ]
  });
};
const ProductPriceWrapper = styled.div(
  ({ align }) => `
  display: flex;
  flex-direction: column;
  align-items: ${align};
`
);
const StyledLatestPrice = styled.div(
  ({ color }) => `
  color: ${color || "rgb(235, 31, 58)"};
  font-weight: 700;
  text-decoration: unset;
  font-size: 15px;
  line-height: 24px;
`
);
const StyledRetailPrice = styled.div`
  text-decoration: line-through;
  color: rgb(130, 134, 158);
  font-weight: 400;
  font-size: 12px;
  line-height: 16px;
`;
const StyledDiscount = styled.div`
  display: flex;
  height: 1rem;
`;

export { ProductPrice };
