import styled from '@emotion/styled';
import { MOBILE_WIDTH } from '../constants.es.js';

const ActionButton = styled.div(({}) => {
  return `
    display: flex;
    justify-content: flex-end;
    align-items: center;
    gap: 16px;
    padding-left: 16px;

    div {
      margin: 0px;
    }

    @media screen and (max-width: ${MOBILE_WIDTH}px){
      display: none;
    }
  `;
});
const ActionButtonMobile = styled.div(
  ({ grid }) => {
    const { navigationTextAlign } = grid;
    let alignItems;
    switch (navigationTextAlign) {
      case "left":
        alignItems = "flex-start";
        break;
      case "right":
        alignItems = "flex-end";
        break;
      case "center":
        alignItems = "center";
        break;
    }
    return `
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    gap: 16px;
    @media screen and (min-width: ${MOBILE_WIDTH}px){
      display: none;
    }

    div {
      // because buttonV2 has a wrapper flex, setting align-item in parent div has no value.
      justify-content: ${alignItems};
      margin: 0px;
    }
  `;
  }
);

export { ActionButton, ActionButtonMobile };
