import { BACKGROUND_CSS_PROPERTIES, BACKGROUND_MARQUEE_DIRECTION_KEY, } from '../../constants';
export const removeAllBackgroundProperties = (styles) => {
    const backgroundProperties = BACKGROUND_CSS_PROPERTIES.map((key) => key.replaceAll('-', '').toUpperCase());
    return Object.entries(styles).reduce((newStyles, [key, value]) => {
        if (!backgroundProperties.includes(key.replaceAll('-', '').toUpperCase())) {
            newStyles[key] = value;
        }
        return newStyles;
    }, {});
};
export const checkExistBackgroundProperties = (styles) => {
    const currentProperties = Object.keys(styles).map((key) => key.replaceAll('-', '').toUpperCase());
    return BACKGROUND_CSS_PROPERTIES.some((key) => {
        return currentProperties.includes(key.replaceAll('-', '').toUpperCase());
    });
};
export const checkExistBackgroundMarqueeDirection = (styles) => {
    const marqueeStyles = Object.entries(styles).find(([key]) => key.replaceAll('-', '').toUpperCase() ===
        BACKGROUND_MARQUEE_DIRECTION_KEY.toUpperCase());
    return !!marqueeStyles;
};
