import { formatImageUrlQuality } from '../../utils/image';
const defaultUrl = 'https://lh3.googleusercontent.com/jkpBgpU-M9jNMa5yXWSj3iRnhYXU2FGWSUnCGw-gaqvQr22O0pv1beWFjBHRO63sIxyPBfnTAOjpJs6ul-GkTolClc78wWnsCg';
describe('format googleusercontent url has no tail quality - formatImageUrlQuality', () => {
    it('format default url - has no tail', async () => {
        const newUrl = formatImageUrlQuality(defaultUrl);
        expect(newUrl).toEqual(defaultUrl + '=w800');
    });
});
describe('format googleusercontent url has specific tail quality - formatImageUrlQuality', () => {
    it("format url has '=rw' tail", async () => {
        const newUrl = formatImageUrlQuality(defaultUrl + '=rw', 200);
        expect(newUrl).toEqual(defaultUrl + '=rw-w200');
    });
    it("format url has '=rw-w1920' tail", async () => {
        const newUrl = formatImageUrlQuality(defaultUrl + '=rw-w1920', 200);
        expect(newUrl).toEqual(defaultUrl + '=rw-w200');
    });
    it("format url has '=w1920' tail", async () => {
        const newUrl = formatImageUrlQuality(defaultUrl + '=w1920', 200);
        expect(newUrl).toEqual(defaultUrl + '=rw-w200');
    });
});
describe('format normal url - formatImageUrlQuality', () => {
    it('format normal url', () => {
        const normalUrl = 'https://picsum.photos/id/237/200/300';
        const newUrl = formatImageUrlQuality(normalUrl, 200);
        expect(normalUrl).toEqual(newUrl);
    });
});
