import { useState, useEffect } from 'react';
import { getIconUrl } from '../mapping.es.js';

const useIcon = (name, isVisible) => {
  const [error, setError] = useState(null);
  const [currIcon, setCurrIcon] = useState(null);
  useEffect(() => {
    const fetchImage = async () => {
      if (!isVisible || !name)
        return;
      try {
        const resIcon = await getIconUrl(name);
        if (!resIcon)
          throw new Error("icon notFound");
        if (resIcon !== currIcon)
          setCurrIcon(resIcon);
      } catch (err) {
        setError(err);
      }
    };
    fetchImage();
  }, [name, isVisible]);
  return {
    error,
    icon: currIcon
  };
};

export { useIcon };
